import './App.css';
import { useState } from "react";
import { evaluate } from "mathjs";

function Button({ text, onClick, disabled = false, className = "Buttons-look" }) {
  return (
    <button
      className={`${className} ${disabled ? "disabled" : ""}`}
      onClick={!disabled ? () => onClick(text) : undefined}
      disabled={disabled}
    >
      {text}
    </button>
  );
}

function Display({ value }) {
  return <div className="display-screen">{value || "0"}</div>;
}

function App() {
  const [input, setInput] = useState("");
  const [isEvaluated, setIsEvaluated] = useState(false);
  const [mode, setMode] = useState("Normal");
  const [question, setQuestion] = useState("");
  const [userAnswer, setUserAnswer] = useState("");
  const [feedback, setFeedback] = useState("");

  const generateQuestion = () => {
    const ops = ["+", "-", "*", "/"];
    const a = Math.floor(Math.random() * 10) + 1;
    const b = Math.floor(Math.random() * 10) + 1;
    const op = ops[Math.floor(Math.random() * ops.length)];
    setQuestion(`${a} ${op} ${b}`);
    setFeedback("");
    setUserAnswer("");
  };

  const handleModeChange = (newMode) => {
    setMode(newMode);
    if (newMode === "Game") {
      generateQuestion();
      setInput(""); // reset display
    }
  };

  const handleClick = (value) => {
    if (isEvaluated) {
      if (!isNaN(value)) setInput(value);
      else setInput((prev) => prev + value);
      setIsEvaluated(false);
    } else {
      setInput((prev) => prev + value);
    }
  };

  const handleEqual = () => {
    try {
      const expression = input.replace(/X/g, "*");
      const result = evaluate(expression);
      setInput(result.toString());
      setIsEvaluated(true);
    } catch {
      setInput("Error");
      setIsEvaluated(true);
    }
  };

  const handleClear = () => {
    setInput("");
    setIsEvaluated(false);
  };

  const handleSubmitAnswer = () => {
    try {
      const correct = evaluate(question);
      if (Number(userAnswer) === correct) {
        setFeedback("‚úÖ Correct!");
      } else {
        setFeedback(`‚ùå Wrong! Correct answer: ${correct}`);
      }
      setTimeout(generateQuestion, 1500);
    } catch {
      setFeedback("Error evaluating question.");
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        {/* üîò Mode toggle bar */}
        <div className="mode-bar">
          <button
            className={`Buttons-mode ${mode === "Normal" ? "active" : ""}`}
            onClick={() => handleModeChange("Normal")}
          >
            Normal Mode
          </button>
          <button
            className={`Buttons-mode ${mode === "Game" ? "active" : ""}`}
            onClick={() => handleModeChange("Game")}
          >
            Game Mode
          </button>
        </div>

        {/* ‚úÖ Calculator display always visible */}
        <Display value={mode === "Game" ? question || input : input} />

        <div className="calculator-grid">
          {/* Disable buttons when in Game Mode */}
          <Button text="7" onClick={handleClick} disabled={mode === "Game"} />
          <Button text="8" onClick={handleClick} disabled={mode === "Game"} />
          <Button text="9" onClick={handleClick} disabled={mode === "Game"} />
          <Button text="X" onClick={handleClick} disabled={mode === "Game"} />

          <Button text="4" onClick={handleClick} disabled={mode === "Game"} />
          <Button text="5" onClick={handleClick} disabled={mode === "Game"} />
          <Button text="6" onClick={handleClick} disabled={mode === "Game"} />
          <Button text="-" onClick={handleClick} disabled={mode === "Game"} />

          <Button text="1" onClick={handleClick} disabled={mode === "Game"} />
          <Button text="2" onClick={handleClick} disabled={mode === "Game"} />
          <Button text="3" onClick={handleClick} disabled={mode === "Game"} />
          <Button text="+" onClick={handleClick} disabled={mode === "Game"} />

          <Button text="0" onClick={handleClick} disabled={mode === "Game"} />
          <Button text="/" onClick={handleClick} disabled={mode === "Game"} />
          <Button text="%" onClick={handleClick} disabled={mode === "Game"} />
          <Button text="=" onClick={handleEqual} disabled={mode === "Game"} />

          <Button text="(" onClick={handleClick} disabled={mode === "Game"} />
          <Button text=")" onClick={handleClick} disabled={mode === "Game"} />
          <Button
            text="CE"
            onClick={handleClear}
            disabled={mode === "Game"}
            className="Buttons-look span-two"
          />
        </div>

        {/* ‚úÖ Game Mode Controls below calculator */}
        {mode === "Game" && (
          <div className="game-mode">
            <h3>Enter your answer:</h3>
            <input
              type="number"
              value={userAnswer}
              onChange={(e) => setUserAnswer(e.target.value)}
              placeholder="Your answer"
              className="game-input"
            />
            <button onClick={handleSubmitAnswer} className="Buttons-look">
              Submit
            </button>
            {feedback && <p className="feedback">{feedback}</p>}
          </div>
        )}
      </header>
    </div>
  );
}

export default App;
