import './App.css';
import { useState } from "react";
import { evaluate } from "mathjs";

function Button({ text, onClick, disabled = false, className = "Buttons-look" }) {
  return (
    <button
      className={`${className} ${disabled ? "disabled" : ""}`}
      onClick={!disabled ? () => onClick(text) : undefined}
      disabled={disabled}
    >
      {text}
    </button>
  );
}

// function Display({ value }) {
//   return <div className="display-screen">{value || "0"}</div>;
// }

function formatNumber(value) {
  const num = Number(value);

  if (!isFinite(num)) return value; // handles Error / N/A

  if (Math.abs(num) >= 1e9) {
    return num.toExponential(3); // 3 significant digits
  }

  return value.toString();
}

function Display({ value }) {
  return (
    <div className="display-screen">
      {value ? formatNumber(value) : "0"}
    </div>
  );
}




function App() {
  const [input, setInput] = useState("");
  const [isEvaluated, setIsEvaluated] = useState(false);
  const [mode, setMode] = useState("Normal");
  const [question, setQuestion] = useState("");
  const [feedback, setFeedback] = useState("");

  // Generate a random math question
  const generateQuestion = () => {
    const ops = ["+", "-", "*", "/", "%"];
    const a = Math.floor(Math.random() * 100) + 1;
    const b = Math.floor(Math.random() * 100) + 1;
    const op = ops[Math.floor(Math.random() * ops.length)];
    setQuestion(`${a}${op}${b}`);
    setFeedback("");
    setInput("");
  };

  // Switch between modes
  const handleModeChange = (newMode) => {
    setMode(newMode);
    setInput("");
    setFeedback("");
    if (newMode === "Game") {
      generateQuestion();
    }
  };

  // Handle calculator button press
  const handleClick = (value) => {
    if (isEvaluated) {
      if (!isNaN(value) || value === ".") setInput(value);
      else setInput((prev) => prev + value);
      setIsEvaluated(false);
    } else {
      setInput((prev) => prev + value);
    }
  };

  //  Handle "=" press
  const handleEqual = () => {
    if (mode === "Normal") {
      try {
        const expression = input.replace(/X/g, "*");
        const result = evaluate(expression);
        setInput(result.toString());
        setIsEvaluated(true);
      } catch {
        setInput("Error");
        setIsEvaluated(true);
      }
    } else if (mode === "Game") {
      try {
        const correct = evaluate(question);
        const userAnswer = Number(input);
        if (userAnswer === correct) {
          setFeedback("✅ Correct!");
        } else {
          setFeedback(`❌ Wrong! (${correct})`);
        }
        // Show feedback briefly, then next question
        setTimeout(() => {
          generateQuestion();
        }, 1500);
        setInput("");
      } catch {
        setInput("Error");
      }
    }
  };

  const handleClear = () => {
    setInput("");
    setFeedback("");
    setIsEvaluated(false);
  };

  return (
    <div className="App">
      <header className="App-header">
        {/* Mode bar */}
        <div className="mode-bar">
          <button
            className={`Buttons-mode ${mode === "Normal" ? "active" : ""}`}
            onClick={() => handleModeChange("Normal")}
          >
            Normal Mode
          </button>
          <button
            className={`Buttons-mode ${mode === "Game" ? "active" : ""}`}
            onClick={() => handleModeChange("Game")}
          >
            Game Mode
          </button>
        </div>

        {/* Display */}
        <Display
          value={
            mode === "Game"
              ? feedback
                ? feedback
                : `Q: ${question}  Ans: ${input || ""}`
              : input
          }
        />

        {/* Calculator grid */}
        <div className="calculator-grid">
          <Button text="7" onClick={handleClick} />
          <Button text="8" onClick={handleClick} />
          <Button text="9" onClick={handleClick} />
          <Button text="X" onClick={handleClick} disabled={mode === "Game"} />

          <Button text="4" onClick={handleClick} />
          <Button text="5" onClick={handleClick} />
          <Button text="6" onClick={handleClick} />
          <Button text="-" onClick={handleClick} disabled={mode === "Game"} />

          <Button text="1" onClick={handleClick} />
          <Button text="2" onClick={handleClick} />
          <Button text="3" onClick={handleClick} />
          <Button text="+" onClick={handleClick} disabled={mode === "Game"} />

          <Button text="0" onClick={handleClick} />
          <Button text="." onClick={handleClick} />
          <Button text="/" onClick={handleClick} disabled={mode === "Game"} />
          <Button text="=" onClick={handleEqual} />

          <Button text="(" onClick={handleClick} disabled={mode === "Game"} />
          <Button text=")" onClick={handleClick} disabled={mode === "Game"} />
          <Button
            text="CE"
            onClick={handleClear}
            className="Buttons-look span-two"
          />
        </div>
      </header>
    </div>
  );
}

export default App;
