import './App.css';
import { useState, useEffect } from "react";
import { evaluate } from "mathjs";

function Button({ text, onClick, disabled = false, className = "Buttons-look" }) {
  return (
    <button
      className={`${className} ${disabled ? "disabled" : ""}`}
      onClick={!disabled ? () => onClick(text) : undefined}
      disabled={disabled}
    >
      {text}
    </button>
  );
}

function Display({ value, onClick }) {
  return (
    <div className="display-screen" onClick={onClick}>
      {value || "0"}
    </div>
  );
}

function App() {
  const [input, setInput] = useState("");
  const [isEvaluated, setIsEvaluated] = useState(false);
  const [mode, setMode] = useState("Normal");

  // Game state
  const [difficulty, setDifficulty] = useState(null);
  const [question, setQuestion] = useState("");
  const [feedback, setFeedback] = useState("");
  const [questionCount, setQuestionCount] = useState(0);
  const [correctCount, setCorrectCount] = useState(0);

  // Timer
  const TOTAL_QUESTIONS = 10;
  const GAME_TIME = 120; // seconds
  const [timeLeft, setTimeLeft] = useState(GAME_TIME);
  const [timerRunning, setTimerRunning] = useState(false);

  // ‚è± Timer effect
  useEffect(() => {
    if (!timerRunning) return;

    if (timeLeft <= 0) {
      endGame();
      return;
    }

    const timer = setInterval(() => {
      setTimeLeft(t => t - 1);
    }, 1000);

    return () => clearInterval(timer);
  }, [timerRunning, timeLeft]);

  // üî¢ Generate question
  const generateQuestion = (level) => {
    let max = 10;
    if (level === "Medium") max = 25;
    if (level === "Hard") max = 50;

    const ops = ["+", "-", "*", "/"];
    const a = Math.floor(Math.random() * max) + 1;
    let b = Math.floor(Math.random() * max) + 1;

    while (b > a) {
      b = Math.floor(Math.random() * max) + 1;
    }

    const op = ops[Math.floor(Math.random() * ops.length)];

    setQuestion(`${a}${op}${b}`);
    setInput("");
    setFeedback("");
  };

  // ‚ñ∂ Start game
  const startGame = (level) => {
    setDifficulty(level);
    setQuestionCount(0);
    setCorrectCount(0);
    setTimeLeft(GAME_TIME);
    setTimerRunning(true);
    generateQuestion(level);
  };

  // ‚èπ Exit game
  const exitGame = () => {
    setDifficulty(null);
    setQuestion("");
    setInput("");
    setFeedback("");
    setQuestionCount(0);
    setCorrectCount(0);
    setTimeLeft(GAME_TIME);
    setTimerRunning(false);
  };

  const endGame = () => {
    setTimerRunning(false);
    const score = Math.round((correctCount / TOTAL_QUESTIONS) * 100);
    setFeedback(`Game Over ‚Äî Score: ${score}%`);
  };

  // üßÆ Calculator input
  const handleClick = (value) => {
    if (isEvaluated) {
      if (!isNaN(value) || value === ".") {
        setInput(value);
      } else {
        setInput(prev => prev + value);
      }
      setIsEvaluated(false);
    } else {
      setInput(prev => prev + value);
    }
  };

  const handleDelete = () => {
    setInput(prev => prev.slice(0, -1));
  };

  const handleClear = () => {
    setInput("");
    setFeedback("");
    setIsEvaluated(false);
  };

  // "="
  const handleEqual = () => {
    if (mode === "Normal") {
      try {
        const result = evaluate(input.replace(/X/g, "*"));
        setInput(result.toString());
        setIsEvaluated(true);
      } catch {
        setInput("Error");
      }
    }

    if (mode === "Game" && difficulty && questionCount < TOTAL_QUESTIONS) {
      try {
        const correct = evaluate(question);
        const userAnswer = Number(input);

        if (userAnswer === correct) {
          setCorrectCount(c => c + 1);
          setFeedback("‚úÖ Correct!");
        } else {
          setFeedback(`‚ùå Wrong! (${correct})`);
        }

        setQuestionCount(q => q + 1);

        setTimeout(() => {
          if (questionCount + 1 >= TOTAL_QUESTIONS) {
            endGame();
          } else {
            generateQuestion(difficulty);
          }
        }, 1200);

      } catch {
        setFeedback("Error");
      }

      setInput("");
    }
  };

  // üñ• Display content
  const getDisplayValue = () => {
    if (mode === "Game" && !difficulty) {
      return (
        <div className="difficulty-picker">
          <button onClick={() => startGame("Easy")}>Easy</button>
          <button onClick={() => startGame("Medium")}>Medium</button>
          <button onClick={() => startGame("Hard")}>Hard</button>
        </div>
      );
    }

    if (mode === "Game") {
      return feedback || `${question} = ${input}`;
    }

    return input;
  };

  return (
    <div className="App">
      <header className="App-header">

        {/* Mode bar */}
        <div className="mode-bar">
          <button
            className={`Buttons-mode ${mode === "Normal" ? "active" : ""}`}
            onClick={() => setMode("Normal")}
          >
            Normal Mode
          </button>
          <button
            className={`Buttons-mode ${mode === "Game" ? "active" : ""}`}
            onClick={() => setMode("Game")}
          >
            Game Mode
          </button>
        </div>

        {/* Timer + Exit */}
        {mode === "Game" && difficulty && (
          <div className="game-header">
            <span>‚è± {timeLeft}s</span>
            <button className="exit-game-btn" onClick={exitGame}>Exit</button>
          </div>
        )}

        {/* Display */}
        <Display value={getDisplayValue()} />

        {/* Calculator */}
        <div className="calculator-grid">
          <Button text="7" onClick={handleClick} />
          <Button text="8" onClick={handleClick} />
          <Button text="9" onClick={handleClick} />
          <Button text="X" onClick={handleClick} disabled={mode === "Game"} />

          <Button text="4" onClick={handleClick} />
          <Button text="5" onClick={handleClick} />
          <Button text="6" onClick={handleClick} />
          <Button text="-" onClick={handleClick} disabled={mode === "Game"} />

          <Button text="1" onClick={handleClick} />
          <Button text="2" onClick={handleClick} />
          <Button text="3" onClick={handleClick} />
          <Button text="+" onClick={handleClick} disabled={mode === "Game"} />

          <Button text="0" onClick={handleClick} />
          <Button text="." onClick={handleClick} />
          <Button text="/" onClick={handleClick} disabled={mode === "Game"} />
          <Button text="=" onClick={handleEqual} />

          <Button text="DEL" onClick={handleDelete} />
          <Button text="CE" onClick={handleClear} />
        </div>

      </header>
    </div>
  );
}

export default App;
