import './App.css';
import { useState } from "react";

function Button({ text, onClick, className = "Buttons-look" }) {
  return (
    <button className={className} onClick={() => onClick(text)}>
      {text}
    </button>
  );
}

function Display({ value }) {
  return (
    <div className="display-screen">
      {value || "0"}
    </div>
  );
}

function App() {
  const [input, setInput] = useState("");

  const handleClick = (value) => {
    setInput((prev) => prev + value);
  };

  const handleEqual = () => {
    if (input.includes("+")) {
      const [num1, num2] = input.split("+"); // split into two parts
      const sum = Number(num1) + Number(num2); // convert to numbers & add
      setInput(sum.toString()); // show result
    } 
    else if(input.includes("-")){
       const [num1, num2] = input.split("+"); // split into two parts
       const sum = Number(num1) - Number(num2); // convert to numbers & add
       setInput(sum.toString()); // show result
    }

    else if(input.includes("*")){
       const [num1, num2] = input.split("+"); // split into two parts
       const sum = Number(num1) * Number(num2); // convert to numbers & add
       setInput(sum.toString()); // show result
    }

    else if(input.includes("/")){
       const [num1, num2] = input.split("+"); // split into two parts
       if(Number(num2)!= 0){
          const sum = Number(num1) / Number(num2); // convert to numbers & add
          setInput(sum.toString()); // show result
       }
       else
        setInput("N/A");
    }

    else if(input.includes("%")){
       const [num1, num2] = input.split("+"); // split into two parts
       const sum = Number(num1) % Number(num2); // convert to numbers & add
       setInput(sum.toString()); // show result
    }


    else {
      setInput(input); // no '+' → just show same value
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <Display value={input} />
        <div className="calculator-grid">

          <Button text="7" onClick={handleClick} />
          <Button text="8" onClick={handleClick} />
          <Button text="9" onClick={handleClick} />
          <Button text="X" onClick={handleClick} />

          <Button text="4" onClick={handleClick} />
          <Button text="5" onClick={handleClick} />
          <Button text="6" onClick={handleClick}/>
          <Button text="-" onClick={handleClick}/>

          <Button text="1" onClick={handleClick} />
          <Button text="2" onClick={handleClick} />
          <Button text="3" onClick={handleClick} />
          <Button text="+" onClick={handleClick}/>

          <Button text="0" onClick={handleClick} />
          <Button text="/" onClick={handleClick}/>
          <Button text="%" onClick={handleClick}/>
          <Button text="=" onClick={handleEqual}/> {/* ✅ now performs addition */}

          <Button text="CE" onClick={() => setInput("")} className="Buttons-look span-four" />
        </div>
      </header>
    </div>
  );
}

export default App;
