import './App.css';
import { useState } from "react";
import { evaluate } from "mathjs";

function Button({ text, onClick, className = "Buttons-look" }) {
  return (
    <button className={className} onClick={() => onClick(text)}>
      {text}
    </button>
  );
}

function Display({ value }) {
  return <div className="display-screen">{value || "0"}</div>;
}

function App() {
  const [input, setInput] = useState("");
  const [isEvaluated, setIsEvaluated] = useState(false);
  const [mode, setMode] = useState("Normal");
  const [question, setQuestion] = useState("");
  const [userAnswer, setUserAnswer] = useState("");
  const [feedback, setFeedback] = useState("");

  // Generate a random math question
  const generateQuestion = () => {
    const ops = ["+", "-", "*", "/", "%"];
    const a = Math.floor(Math.random() * 10) + 1;
    const b = Math.floor(Math.random() * 10) + 1;
    const op = ops[Math.floor(Math.random() * ops.length)];
    setQuestion(`${a} ${op} ${b}`);
    setFeedback("");
    setUserAnswer("");
  };

  // When switching to Game Mode, generate one
  const handleModeChange = (newMode) => {
    setMode(newMode);
    if (newMode === "Game") generateQuestion();
  };

  // Calculator logic
  const handleClick = (value) => {
    if (isEvaluated) {
      if (!isNaN(value)) setInput(value);
      else setInput((prev) => prev + value);
      setIsEvaluated(false);
    } else {
      setInput((prev) => prev + value);
    }
  };

  const handleEqual = () => {
    try {
      const expression = input.replace(/X/g, "*");
      const result = evaluate(expression);
      setInput(result.toString());
      setIsEvaluated(true);
    } catch {
      setInput("Error");
      setIsEvaluated(true);
    }
  };

  const handleClear = () => {
    setInput("");
    setIsEvaluated(false);
  };

  // Handle game answer
  const handleSubmitAnswer = () => {
    try {
      const correct = evaluate(question);
      if (Number(userAnswer) === correct) {
        setFeedback("✅ Correct!");
      } else {
        setFeedback(`❌ Wrong! Correct answer: ${correct}`);
      }
      setTimeout(generateQuestion, 1500);
    } catch {
      setFeedback("Error evaluating question.");
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <div className="mode-bar">
          <button
            className={`Buttons-mode ${mode === "Normal" ? "active" : ""}`}
            onClick={() => handleModeChange("Normal")}
          >
            Normal Mode
          </button>
          <button
            className={`Buttons-mode ${mode === "Game" ? "active" : ""}`}
            onClick={() => handleModeChange("Game")}
          >
            Game Mode
          </button>
        </div>

        {/*  NORMAL MODE UI */}
        {mode === "Normal" && (
          <>
            <Display value={input} />
            <div className="calculator-grid">
              <Button text="7" onClick={handleClick} />
              <Button text="8" onClick={handleClick} />
              <Button text="9" onClick={handleClick} />
              <Button text="X" onClick={handleClick} />

              <Button text="4" onClick={handleClick} />
              <Button text="5" onClick={handleClick} />
              <Button text="6" onClick={handleClick} />
              <Button text="-" onClick={handleClick} />

              <Button text="1" onClick={handleClick} />
              <Button text="2" onClick={handleClick} />
              <Button text="3" onClick={handleClick} />
              <Button text="+" onClick={handleClick} />

              <Button text="0" onClick={handleClick} />
              <Button text="/" onClick={handleClick} />
              <Button text="%" onClick={handleClick} />
              <Button text="=" onClick={handleEqual} />

              <Button text="(" onClick={handleClick} />
              <Button text=")" onClick={handleClick} />
              <Button text="CE" onClick={handleClear} className="Buttons-look span-two" />
            </div>
          </>
        )}

        {/* GAME MODE UI */}
        {mode === "Game" && (
          <div className="game-mode">
            <h2>Solve:</h2>
            <div className="game-question">{question}</div>
            <input
              type="number"
              value={userAnswer}
              onChange={(e) => setUserAnswer(e.target.value)}
              placeholder="Your answer"
              className="game-input"
            />
            <button onClick={handleSubmitAnswer} className="Buttons-look">
              Submit
            </button>
            {feedback && <p className="feedback">{feedback}</p>}
          </div>
        )}
      </header>
    </div>
  );
}

export default App;
